import React, { Component } from 'react';
import { Container, Form, Button } from 'semantic-ui-react';
import { ToastContainer } from 'react-toastify';
import {getZkSnarkInstance} from '../config/instances/contractInstances';
import web3 from '../config/web3/web3';

class Index extends Component {

    ontransfer = async () => {
        event.preventDefault();
        try {
            const accounts = await web3.eth.getAccpunts()
            const zkInstance = await getZkSnarkInstance(web3);
            const hash = await zkInstance.methods.transferNoteToContract().send({
                from: accounts[0]
            })
            console.log("ZkInstance: ", zkInstance);
        } catch (error) {
            console.log(error)
        }
    };

    onClaim = async () => {
