    };

    render() {
        return (
          <div>
            <ToastContainer/>
            <Container>
                <Form onSubmit={this.ontransfer}>
                    <Button>transfer</Button>
                </Form>
                
                <Form onSubmit={this.onClaim}>
                    <Button>claim</Button>
                </Form>
            </Container>
          </div>
        );
    }
}

export default Index;
